import React, { Component } from 'react';
import ActiveFriends from './active';
import InactiveFriends from './inactive';



class UserForm extends Component {
  constructor() {
    super()
    
    this.state = {
      member:[
               {name:"Temmy", active: true},
               {name:"kola", active: false}
             ],
      firstname:"",
      lastname:"",
      facebook:"",
      twitter: "",
      contact:"",
      address:"",
      input:""
   }
   this.handleAdd=this.handleAdd.bind(this);
   this.handleChange=this.handleChange.bind(this);
 }
 
  handleAdd(){
    if(this.state.input === ""){
      return 0;
    }

    this.setState((prevState)=>{
      return{
             /// Check if the User already exist than add users
        member: prevState.member.filter((friends)=> friends.name !== this.state.input).concat({
        name:this.state.input,
        active:true
        }) 
      }
    })
  

  }

  handleChange(e){
    const value = e.target.value;
    this.setState({input:value})
  }

  render() {
    return (
     <div>
        
        <div>
          <label> First Name: 
            <input type="text"
                  onChange ={this.handleChange}
            />
          </label>
        </div>

        <div>
          <label>Last Name:
            <input type="text"
                  
              />
          </label>
        </div>
              
        <div>
          <label> Email:
            <input type="text"
                      
            />
          </label>
        </div>
    

        <div>
        <label> Mobile:
            <input type="text"
                    
            />
        </label>
        </div>
      
        <div>
        <label> Facebook:
          <input type="text"
                    
          />
          </label>    
        </div>

        <div>
        <label> Instagram:
          <input type="text"
                    
          />
          </label>    
        </div>

        <div>
        <label> Twitter:
          <input type="text"
                    
          />
          </label>    
        </div>


        <div>
        <label> Address:
          <input type="text"
                    
          />
          </label>    
        </div>
    
      <button  >Add</button>
   
        <hr/>
        <ActiveFriends 
        activeList={this.state.member.filter(status=> status.active)}
        />
        <hr/>
        <InactiveFriends/>
    </div>  
     
    );
  }
}





export default UserForm;
