import React, { Component } from 'react';
import UserForm from './components/userForm';
import ActiveFriends from './components/active';
import InactiveFriends from './components/inactive';
import Friends from "./components/friends";



class App extends Component {
 constructor() {
   super()

   this.state = {
      member:[],
      firstname:"",
      lastname:"",
      facebook:"",
      twitter: "",
      contact:"",
      address:"",
      input:""
   }
   this.handleAdd=this.handleAdd.bind(this);
   this.handleChange=this.handleChange.bind(this);
 }

  handleAdd(){

    if(this.state.input === ""){
      return 0;
    }

    this.setState((prevState)=>{
      return{
             /// Check if the User already exist than add users
        member: prevState.member.filter((friends)=> friends.name !== this.state.input).concat({
        name:this.state.input,
        active:true
        }) 
      }
    })


  }

  handleChange(e){
    const value = e.target.value;
    this.setState({input:value})
  }

  render() {
    return (
     <div>

        <UserForm  changeEvent ={this.handleAdd}/>
        <hr/>
        <ActiveFriends/>
        <hr/>
        <InactiveFriends/>
        <Friends friendsList={this.state.member}/>
     </div>
    );
  }
}

export default App;
