import React, { Component } from 'react';
import UserForm from './components/userForm';
import ActiveFriends from './components/active';
import InactiveFriends from './components/inactive';



class App extends Component {
  constructor() {
    super()

    this.state = {
      member:[ ],
      firstname:"",
      lastname:"",
      email:"",
      mobile:"",
      fbook:"",
      insta:"",
      twit: "",
      address:"",

   }
   this.handleAdd=this.handleAdd.bind(this);
   this.handleChange=this.handleChange.bind(this);
 }

  handleAdd(){
    if(this.state.email === ""){
      return 0;
    }

    this.setState((prevState)=>{
      return{
             /// Check if the User already exist than add users
        member: prevState.member.filter((friends)=> friends.email !== this.state.email).concat({
        firstName:this.state.firstname,
        lastName:this.state.lastname,
        email:this.state.email,
        mobile:this.state.mobile,
        fbook:this.state.fbook,
        insta:this.state.insta,
        twit:this.state.twit,
        twit:this.state.twit,
        address:this.state.address,    
        active:true
        }) 
      }
    })


  }

  handleChange(e){
    const value = e.target.value;
    const object= e.target.name;
    this.setState({[object]:value})
  }

  render() {
    return (
     <div>

        <UserForm 
        action={this.handleChange}
        buttonAction={this.handleAdd}/>
        <hr/>
        <ActiveFriends 
          activeList={this.state.member.filter(status=> status.active)}
        />
        <hr/>
        <InactiveFriends/>
    </div>  

    );
  }
}
export default App;
