import React from 'react';
import UserForm from './components/userForm';
import ActiveFriends from './components/active';
import InactiveFriends from './components/inactive';



class App extends React.Component {
  constructor() {
    super()

    this.state = {
      member:[],
      firstname:"",
      lastname:"",
      email:"",
      mobile:"",
      fbook:"",
      insta:"",
      twit: "",
      address:"",

   }
   this.handleAdd=this.handleAdd.bind(this);
   this.handleChange=this.handleChange.bind(this);
   this.handleClear=this.handleClear.bind(this);
   this.handleToggle=this.handleToggle.bind(this);
 }

  handleAdd(){
    if(this.state.firstname === "" || this.state.lastname === ""||this.state.email === ""||this.state.fbook === ""||this.state.twit === "" || this.state.address === ""|| this.state.twit === ""){
      return 0;
    }



    this.setState((prevState)=>{
     return{
        /// Check if the User already exist than add users
          member: prevState.member.filter(friends => 
            friends.firstname  !== this.state.firstname || friends.lastname !== this.state.lastname || friends.email !== this.state.email
          ).concat([{
          firstname:this.state.firstname,
          lastname:this.state.lastname,
          email:this.state.email,
          mobile:this.state.mobile,
          fbook:this.state.fbook,
          insta:this.state.insta,
          twit:this.state.twit,
          address:this.state.address,
          active: true
          }]) 
          //#endregion

        }

    })


  }

  handleChange(e){
    const value = e.target.value;
    const object= e.target.name;
    this.setState({
      [object]:value,

    })

  }

  handleToggle(email){
     this.setState(prevState=>{
      const friendName = prevState.member.find((listNames)=>listNames.email === email)


    return{
      member: prevState.member.filter((friend)=>friend.email !== email).concat([{

        email,
        active: !friendName.active
      }])
    }
  })

  }

  handleClear(e){
    e.target.value = " ";
    const object= e.target.name;

    this.setState({
      [object]:" ",

    })

  }

  render() {
    return (
     <div>
       <UserForm 
        action={this.handleChange}
        clear={this.handleClear}
        buttonAction={this.handleAdd}/>
        <hr/>
        <ActiveFriends 
          activeList={this.state.member.filter(status=> status.active===true)}
          onToggle={this.handleToggle}
        />
        <hr/>
        <InactiveFriends inactive={this.state.member.filter(status=> status.active === false)}/>
    </div>  

    );
  }
}
export default App;
